"use strict";
// Understanding Static Types
// JS is dynamically typed. JS biggest paradox that values 
// have types instead of variables. e.g.,
let myVar;
console.log(`${myVar} = ${typeof myVar}`);
myVar = 12;
console.log(`${myVar} = ${typeof myVar}`);
myVar = "Hello";
console.log(`${myVar} = ${typeof myVar}`);
myVar = true;
console.log(`${myVar} = ${typeof myVar}`);
